import React, { useState, useEffect } from "react";
import { tsvParse } from "d3-dsv";
import { timeParse } from "d3-time-format";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";
function parseData(parse) {
    return function (d) {
      d.date = parse(d.date);
      d.open = +d.open;
      d.high = +d.high;
      d.low = +d.low;
      d.close = +d.close;
      d.volume = +d.volume;
  
      return d;
    };
  }
const parseDate = timeParse("%Y-%m-%d");
const fetchData = async () => {
    try {
      const response = await fetch("https://cdn.rawgit.com/rrag/react-stockcharts/master/docs/data/MSFT.tsv");
      const data = await response.text();
      const parsedData = tsvParse(data, parseData(parseDate));
      setStockData(parsedData);
    } catch (error) {
      console.error("Error fetching stock data:", error);
    }
  };
   

const StockChart = () => {
  const [stockData, setStockData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getData();
        setStockData(data);
      } catch (error) {
        console.error("Error fetching stock data:", error);
      }
    };

    fetchData();
  }, []);

  if (!stockData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Stock Chart</h2>
      <LineChart width={600} height={400} data={stockData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        <XAxis dataKey="date" />
        <YAxis />
        <CartesianGrid strokeDasharray="3 3" />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="close" stroke="#8884d8" />
      </LineChart>
    </div>
  );
};

export default StockChart;
